
public class exercise23527 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		int j;
		int k;
		
		for (i = 1; i <= 5; i++) {
			for (j = 1; j <= 3; j++) {
				for (k = 1; k <= 4; k++) {
					System.out.print("*");
				}
				
				System.out.println();
			}
			
			System.out.println();
		}
	}

}
